import {useFormik} from "formik";
import *as yup from "yup";
const formik=useFormik({
    initialValues:{
        FirstName:'',
        SecondName:'',
        PhoneNumber:'',
        Password:'',
        ConfirmPassword:''
    },
    onsubmit:(values) =>
    {
      console.log(values);
    }
})
export default formik;
