    initialValues:{
        FirstName:'',
        SecondName:'',
        PhoneNumber:'',
        Password:'',
        ConfirmPassword:''
    },
     onSubmit:(values) =>
    {
      console.log(values);
    }
})
  return (
    <form onSubmit={formik.handleSubmit}>
    <input
    type="password"
    name="Password"
    className="form-control"
    autoComplete="off"
    onChange={formik.handleChange}
    value={formik.values.Password}
    />
    <Button>onSubmit</Button>
    </form>
  );
}

export default App;
